int pp(int** a, int size);

typedef struct {
    int x;
    int y;
} site;
int ERR = 0;
typedef struct
{
    site* arr;
    int head;
    int rear;
    int len;
    int size;
    int fullflag;
} queue;

void callocQueue(queue* q, int size)
{
    q->arr = (site*)calloc(size, sizeof(site));
    q->head = 0;
    q->rear = 0;
    q->len = 0;
    q->size = size;
    q->fullflag = 0;
}
void freeQueue(queue* q)
{
    free(q->arr);
}
int isEmptyQue(queue* q)
{
    return (q->head == q->rear && !q->fullflag);
}
int pushQue(queue* q, int x, int y)
{
    if (!q->fullflag) {
        q->arr[q->rear].x = x;
        q->arr[q->rear].y = y;
        q->rear = (q->rear + 1) % (q->size);
        if (q->rear == q->head) {
            q->fullflag = 1;
        }
        return 1;
    } else {
        ERR++;
        return 0;
    }
}
int popQue(queue* q, int* x, int* y)
{
    *x = q->arr[q->head].x;
    *y = q->arr[q->head].y;
    if (q->fullflag) {
        q->fullflag = 0;
    }
    q->head = (q->head + 1) % q->size;
    return q->head;
}

int** A;
int ASize;

//int** IslandMap;
int** BfsMap;
queue BfsQue;

void callocMap(int*** map, int mapSize)
{
    (*map) = (int**)calloc(mapSize, sizeof(int*));
    int* head = (int*)calloc(mapSize * mapSize, sizeof(int));
    for (size_t i = 0; i < mapSize; i++) {
        (*map)[i] = head + i * mapSize;
    }
}
void freeMap(int** map)
{
    free(map[0]);
    free(map);
}

void findOneIsland(int* cx, int* cy)
{
    for (size_t i = 0; i < ASize; i++) {
        for (size_t j = 0; j < ASize; j++) {
            if (A[i][j] == 1) {
                *cx = i;
                *cy = j;
                return;
            }
        }
    }
}
int inArea(int cx, int cy)
{
    return (cx >= 0 && cx < ASize && cy >= 0 && cy < ASize);
}
void initBfsMap(int cx, int cy)
{
    for (size_t i = 0; i < ASize; i++) {
        for (size_t j = 0; j < ASize; j++) {
            BfsMap[i][j] = INT_MAX;
        }
    }

    queue que;
    callocQueue(&que, MaxSize * MaxSize);
    pushQue(&que, cx, cy);
    while (!isEmptyQue(&que)) {
        popQue(&que, &cx, &cy);
        if (inArea(cx, cy) && A[cx][cy] == 1 && BfsMap[cx][cy] == INT_MAX) {
            BfsMap[cx][cy] = 1;
            pushQue(&BfsQue, cx, cy);
            pushQue(&que, cx + 1, cy + 0);
            pushQue(&que, cx - 1, cy + 0);
            pushQue(&que, cx + 0, cy + 1);
            pushQue(&que, cx + 0, cy - 1);
        } else {
            continue;
        }
        showMatrix(BfsMap, ASize, ASize);
    }
    freeQueue(&que);
    showMatrix(A, ASize, ASize);
    getchar();
    showMatrix(BfsMap, ASize, ASize);
    getchar();
    // pp(A, ASize);
    // pp(BfsMap, ASize);
}
int isNewLand(int cx, int cy)
{
    return (A[cx][cy] == 1 && BfsMap[cx][cy] == INT_MAX);
}

int searchNewLand()
{
    int cx, cy;
    int dt[4][2] = { { 0, 1 }, { 0, -1 }, { 1, 0 }, { -1, 0 } };
    int dx, dy;
    while (!isEmptyQue(&BfsQue)) {
        popQue(&BfsQue, &cx, &cy);
        int step = BfsMap[cx][cy] + 1;
        for (size_t i = 0; i < 4; i++) {
            dx = cx + dt[i][0];
            dy = cy + dt[i][1];
            if (!inArea(dx, dy)) {
                continue;
            } else if (isNewLand(dx, dy)) {
                // pp(BfsMap, ASize);
                getchar();
                showMatrix(BfsMap, ASize, ASize);
                printf("%d,%d\n", dx, dy);
                return (step - 2);
            } else if (BfsMap[dx][dy] == INT_MAX) {
                {
                    pushQue(&BfsQue, dx, dy);
                    BfsMap[dx][dy] = BfsMap[dx][dy] < step ? BfsMap[dx][dy] : step;
                }
            }
            // showMatrix(BfsMap,ASize,ASize);
        }
    }
    return -1;
}

int shortestBridge(int** a, int aSize)
{
    A = a;
    ASize = aSize;
    int cx, cy;
    callocMap(&BfsMap, ASize);
    callocQueue(&BfsQue, MaxSize * MaxSize);

    findOneIsland(&cx, &cy);

    initBfsMap(cx, cy);

    int rt = searchNewLand(cx, cy);
    freeMap(BfsMap);
    freeQueue(&BfsQue);
    printf("%d", rt);
    return rt;
}

