int pp(int** a, int size)
{
    printf("%d\n", ERR);
    printf("   ");
    for (size_t i = 0; i < size; i++) {
        printf("%d ", i % 10);
    }
    printf("\n");

    for (size_t i = 0; i < size; i++) {
        printf("%d |", i % 10);
        for (size_t j = 0; j < size; j++) {
            if (a[i][j] == INT_MAX || a[i][j] == 0) {
                printf(". ");

            } else
                printf("%d ", a[i][j]);
        }
        printf("\n");
    }
    printf("\n");
}

